      <Container>
            <Button onClick={this.setState({selectedBook:book})}>Sci - Fi</Button>
        <Row className="row-cols-sm-1 row-cols-md-2 row-cols-lg-4">

          {books.map((book) => (
            <Col>
              <Card >
                <Card.Img variant="top" src= {book.img}/>
                <Card.Body key={book.asin}>
                  <Card.Title>{book.title}</Card.Title>
                  <Card.Text>
                   {book.category}
                  </Card.Text>

                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    );
  }
}
